import React, { Component } from "react";
import Post from "../../components/Post";
import "./home.css";

type PostDetails = {
  id: string;
  title: string;
  description: string;
  hoursCount?: number;
  lecturerName?: string;
  tag: string[];
};

type HomeProps = {};

type HomeState = {
  postList: PostDetails[];
};

export default class Home extends Component<HomeProps, HomeState> {
  constructor(props: HomeProps) {
    super(props);
    this.state = {
      postList: [
        {
          id: "1",
          title: "First Post",
          description: "This is first post on wall",
          hoursCount: 8,
          lecturerName: "Sanu",
          tag: ["Introduction"],
        },
        {
          id: "2",
          title: "Second Post",
          description: "This is Second post on wall",
          hoursCount: 8,
          lecturerName: "Yashith",
          tag: ["React", "TypeScrypt"],
        },
        {
          id: "3",
          title: "Third Post",
          description: "This is Third post on wall",
          hoursCount: 8,
          lecturerName: "Yashith",
          tag: ["TailwindCss", "NodeJs", "MongoDB"],
        },
      ],
    };
  }

  render() {
    return (
      <Fragment>
      <header/>
      <div className="p-6">
        {this.state.postList.map((post) => (
          <Post
            key={post.id}
            title={post.title}
            description={post.description}
            tags={post.tag}
            hoursCount={post.hoursCount}
            lecturerName={post.lecturerName}
          />
        ))}
      </div>
      </Fragment>
    );
  }
}
