import React, { Component, Fragment } from 'react'
import Header from '../../components/Header'
import Post from '../../components/Post';
import { PostDetails } from '../../type/PostDetails';
import './profile.css'
import AddCircleIcon from '@mui/icons-material/AddCircle';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';
import { TextField } from '@mui/material';
import PostAddIcon from '@mui/icons-material/PostAdd';

type ProfileProps = {};

type ProfileState = {
  postList: PostDetails[];
  isClickedCreateNewPost: boolean;
}

handleClickCreateNewPost  = () => {}

export default class Profile extends Component<ProfileProps, ProfileState> {
  constructor(props: ProfileProps) {
    super(props);
    this.state = {
      isClickedCreateNewPost: false,
      postList: [
        {
          id: "1",
          title: "Hello World!",
          description: "This is first post on wall",
          hoursCount: 8,
          lecturerName: "Sanu",
          tag: ["Introduction"],
        },
        {
          id: "2",
          title: "Java",
          description: "This is Second post on wall",
          hoursCount: 8,
          lecturerName: "Yashith",
          tag: ["OOP", "Layered Architecture"],
        },
        {
          id: "3",
          title: "JavaScript",
          description: "This is Third post on wall",
          hoursCount: 4,

          tag: ["Front-End Developments", "NodeJs", "React with TypeScript"],
        },
      ],
    };
  }

  render() {
    return (
      <Fragment>
        <Header/>
        <div className='mt-24 px-6'>
          {this.state.isClickedCreateNewPost ? (
            <>
            <div onClick={this.handleClickCreateNewPost()} className='p-6 w-full rounded-lg bg-purple-900 shadow-lg hover:bg-purple-700 flex text-white font-bold justify-between items-center'>
              <h6 className='text-xl'>Create a Post</h6>
              <AddCircleIcon/>
            </div>
          </>

          ) : (
            <>
            <div className='p-6 w-full rounded-t-lg bg-purple-900 shadow-lg hover:bg-purple-700 flex text-white font-bold justify-between items-center'>
              <h6 className='text-xl'>Discard Post</h6>
              <RemoveCircleIcon/>
            </div>
            <div className='p-6 w-full rounded-b-lg bg-purple-300 shadow-lg flex text-white font-bold justify-between items-center'>
              <form className='w-full p-6 space-y-4'>
                <TextField className='bg-white rounded-md'
                  label='Post Title'
                  type='text'
                  variant='outlined'
                  placeholder='Enter Title Here'
                  fullWidth={true}
                  required
                />
                <TextField className='bg-white rounded-md'
                  label='Description'
                  type='text'
                  variant='outlined'
                  placeholder='Enter Description Here'
                  fullWidth={true}
                  required
                />
                <TextField className='bg-white rounded-md'
                  label='Lecturer Name'
                  type='text'
                  variant='outlined'
                  placeholder='Enter Lecturer Name'
                  fullWidth={true}
                />
                <TextField className='bg-white rounded-md'
                  label='Lecture Hours'
                  type='number'
                  variant='outlined'
                  placeholder='Enter Lecture Hours'
                  fullWidth={true}
                />
                <TextField className='bg-white rounded-md'
                  label='Tags'
                  type='number'
                  variant='outlined'
                  placeholder='Enter Tags with ,'
                  fullWidth={true}
                />
                <button className='w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-purple-700 rounded-md hover:bg-purple-600 focus:outline-none focus:bg-purple-600'>
                  <PostAddIcon/>
                  Post
                </button>
              </form>
            </div>
          </>
          )}
        </div>
        <div className='p-6'>
        {this.state.postList.map((post) => (
          <Post
            key={post.id}
            title={post.title}
            description={post.description}
            tags={post.tag}
            hoursCount={post.hoursCount}
            lecturerName={post.lecturerName}
          />
        ))}
        </div>
      </Fragment>
    );
  }
}
