import React, { Component } from "react";
import Tag from "../Tag";

type PostProps = {
  title: string;
  description: string;
  hoursCount?: number;
  lecturerName?: string;
  tags: string[];
};

type PostState = {};

export default class Post extends Component<PostProps, PostState> {
  render() {
    return (
      <div className="p-6 mt-5 w-full rounded-md bg-white shadow-lg hover:bg-purple-400">
        <h3 className="select-none text-center pb-3 text-2xl font-bold">
          {this.props.title}
        </h3>
        <p className="select-none">{this.props.description}</p>
        {this.props.lecturerName ? (
          <p>Lecturer Name : {this.props.lecturerName}</p>
        ) : null}
        {this.props.hoursCount ? (
          <p>Lecture Hours : {this.props.hoursCount}</p>
        ) : null}
        <span className="w-full mt-3 flex space-x-3">
          {/* {this.props.tags.map((title) => (
          <Tag title={title} />
        ))} */}

          {this.props.tags.map((tagText) => (
            <Tag title={tagText} />
          ))}
        </span>
      </div>
    );
  }
}
