    title: string;
    description: string;
    tags: string[];
}

type PostState = {};

export default class Post extends Component<PostProps, PostState> {
  render() {
    return (
      <div className='p-6 mt-5 border border-stone-700 w-full'>
        <h3 className='text-center pb-3'>{this.props.title}</h3>
        <p>{this.props.description}</p>
        <span className='w-full mt-3 flex space-x-3'>
            <Tag tit/>
        </span>
      </div>
    )
  }
}
