        <div className=' border-2 border-red-600'></div>

        <div>
            <button className='hover:bg-green-800 hover:text-white hover:drop-shadow-2xl border-2 border-green-800 text-green-800 px-4 py-2 mx-2 my-4 rounded-xl'>Increment</button>
            <button className='hover:bg-red-800 hover:text-white hover:drop-shadow-2xl border-2 border-red-800 text-red-800 px-4 py-2 mx-2 my-4 rounded-xl'>Decrement</button>
        </div>

    </div>;
  }
}
