import AboutCard from '../../components/AboutCard'
import './about.css'

type AboutCardDetails = {
  id: string;
  title: string;
  description: string;
}

type AboutProps = {};

type AboutState = {
  cardList: AboutCardDetails[];
};

export default class About extends Component<AboutProps, AboutState> {

  constructor(props: AboutProps) {
    super(props);
    this.state = {
      cardList: [
        {
          id: '1',
          title: 'Ui/Ux Design',
          description: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Eaque, sit. Corrupti libero error quibusdam ad. Nihil reprehenderit odio inventore mollitia ducimus ipsa animi cupiditate beatae',
        },
        {
          id: '2',
          title: 'Front End Development',
          description: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Eaque, sit. Corrupti libero error quibusdam ad. Nihil reprehenderit odio inventore mollitia ducimus ipsa animi cupiditate beatae',
        },
        {
          id: '3',
          title: 'Back End Development',
          description: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Eaque, sit. Corrupti libero error quibusdam ad. Nihil reprehenderit odio inventore mollitia ducimus ipsa animi cupiditate beatae',
        },
        {
          id: '4',
          title: 'Graphics Design',
          description: 'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Eaque, sit. Corrupti libero error quibusdam ad. Nihil reprehenderit odio inventore mollitia ducimus ipsa animi cupiditate beatae',
        }
      ]
    }
  }

  render() {
    return (
      <div className="p-6">
        <h1 className='text-center text-4xl font-bold pb-5'>About Us</h1>
        <p className='font-semibold text-justify text-lg pb-5'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Lorem ipsum dolor sit, amet consectetur adipisicing elit. Eaque, sit. Corrupti libero error quibusdam ad. Nihil reprehenderit odio inventore mollitia ducimus ipsa animi cupiditate beatae ipsum impedit quidem, dolore officia. Lorem ipsum dolor sit amet consectetur adipisicing elit. Ex, debitis consequuntur, ad magnam quam sit quas perspiciatis alias nemo laudantium cumque asperiores minima sed nulla illo pariatur. Expedita, voluptate soluta. Lorem ipsum dolor sit amet consectetur adipisicing elit. Odio corrupti, voluptates fuga nam accusantium vero pariatur eaque. Asperiores, earum pariatur. Molestiae aperiam facilis veritatis. Explicabo cupiditate odit asperiores molestias accusantium! Lorem ipsum dolor sit, amet consectetur adipisicing elit. Consequatur inventore totam atque mollitia vel, suscipit magni enim quo ipsa explicabo fuga, quisquam nulla quas quaerat cum neque voluptas id perspiciatis.</p>
        <div className=' w-full grid grid-cols-4 gap-4'>
          {this.state.cardList.map((card) => (
            <AboutCard key={card.id} title={card.title} description={card.description}/>
          ))}
        </div>
        
      </div>
    )
  }
}
