import React, { Component } from 'react'

type counterProps = {};

type counterState = {
    count: number;
};

export default class Counter extends Component<counterProps, counterState> {
    constructor(props: counterProps){
        super(props);
        this.state = {
            count: 0
        }
        //normal widiyta funtction eka gaththanm mehema karanna oona. arrow function ekedi oona na
        this.increaseCount = this.increaseCount.bind(this);
    }

    //normal widiyta gaththu funtction eka
    increaseCount(){

    }

    //arrow function eka
    increaseCount = () => {
        this.setState({count: this.state.count+1});
    };

    //arrow function eka
    decreaseCount = () => {
        this.setState({count: this.state.count-1});
    };

  render() {
    return <div className='bg-white rounded-md md:text-center m-10 '>
        <h1 className='text-6xl p-4 font-semibold'>Count: {this.state.count}</h1>
        <div className=' border-2 border-red-600'></div>

        <div>
            <button onClick={this.increaseCount} className='hover:bg-green-800 hover:text-white hover:drop-shadow-2xl border-2 border-green-800 text-green-800 px-4 py-2 mx-2 my-4 rounded-xl'>Increment</button>
            <button onClick={this.decreaseCount} className='hover:bg-red-800 hover:text-white hover:drop-shadow-2xl border-2 border-red-800 text-red-800 px-4 py-2 mx-2 my-4 rounded-xl'>Decrement</button>
        </div>

    </div>;
  }
}
